package logical;

public class logical_opt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// && operator
		System.out.println((5>3)&&(8>3));
		System.out.println((5>3)&&(2>3));
		// || operator
				System.out.println((5>3)||(8>3));
				System.out.println((5>3)||(2>3));
			
				// ! operator
				System.out.println((5==3));
				
			
	}

}
